import React from 'react';
import { styled } from '@mui/system';
import { Drawer, Typography, Paper} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { List, ListItem, ListItemIcon, ListItemText } from '@mui/material';
import { AddCircleOutlineOutlined, SubjectOutlined } from '@mui/icons-material';
import {AppBar, Toolbar} from '@mui/material';


const drawerWidth = 240;

const CustomPage = styled('div')({
  flexGrow: 1,
  padding: '20px',
  minHeight: '100vh', // 确保页面至少占满整个视窗高度
});

const CustomDrawer = styled(Drawer)({
  width: drawerWidth,
  flexShrink: 0,
  '& .MuiDrawer-paper': {
    width: drawerWidth,
    boxSizing: 'border-box',
  },
});

const CustomDrawerRoot = styled('div')({
  display: 'flex',
});

const CustomAppBar = styled(AppBar)({
    width: `calc(100% - ${drawerWidth}px)`
})

const CustomToolbar = styled(Toolbar)({
    height: 20
})

const menuItems = [
    {
        text: 'My Notes',
        icon: <SubjectOutlined color='secondary'/>,
        path: '/'
    },
    {
        text: 'Create Notes',
        icon: <AddCircleOutlineOutlined />,
        path: '/create'
    }
]


const Layout = ({ children }) => {

  const navigate = useNavigate();

  return (
    // app bar 
    <div>
        {/* elevation 没有阴影 */}
        <CustomAppBar elevation={0}>  
            <CustomToolbar>
                <Typography>
                    Welcome to Notes
                </Typography>
            </CustomToolbar>
        </CustomAppBar>

        // permanent drawer
        <CustomDrawerRoot>
            <CustomDrawer variant='permanent' anchor='left'>
                <Paper>
                    <Typography variant='h5' style={{ padding: 16 }}>
                        Jiting's Note
                    </Typography>
                </Paper>

                {/* List of links */}
                <List>
                    {
                        menuItems.map(item => 
                            <ListItem
                                button
                                key = {item.text}
                                onClick={() => navigate(item.path)}
                                >
                                <ListItemIcon>{item.icon}</ListItemIcon>
                                <ListItemText primary={item.text}></ListItemText>
                            </ListItem>)
                    }
                </List>
            </CustomDrawer>
            <CustomPage>
                {/* 在展示 Notes 的 div 中插入一个 CustomToobar, 这样 Toolbar 就不会压着 Notes 了。  */}
                <CustomToolbar />
                {children}
            </CustomPage>
        </CustomDrawerRoot>
    </div>

  );
};

export default Layout;
